/* yypllex_destroy is for both reentrant and non-reentrant scanners. */
int yypllex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		yypl_delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		yyplpop_buffer_state();
	}

	/* Destroy the stack itself. */
	yyplfree((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * yypllex() is called, initialization will occur. */
    yy_init_globals( );

    return 0;
}

